import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from '../contexts/AuthContext';
import styled from 'styled-components';
import logo from '../ui/SCHOLLI_logo.jpeg';

const User = {
    email: 'kimkt@ewhain.net',
    pw: 'ewha1234@@'
}

const Page = styled.div`
    position: absolute;
    top: 0;
    bottom: 0;
    width: 100%;
    max-width: 500px;
    padding: 0 20px;
    left: 50%;
    transform: translate(-50%, 0);
    background-color: #f7f7f7;
    overflow: hidden;
    display: flex;
    flex-direction: column;
    align-items: center;
`;

const TitleWrap = styled.div`
    margin-top: 40px;
    width: 100%;

    img {
        height: 60px; /* 로고 크기 */
        margin-bottom: 16px;
        display: block; 
        margin-bottom: 70px;
        margin-left: auto;
        margin-right: auto;
    }

    h1 {
        font-size: 20px;
        font-weight: 600;
        color: #348a8c;
        margin-bottom: 5px; 
        margin-left: 5px;
    }
`;

const ContentWrap = styled.div`
    margin-top: 26px;
    flex: 1;
    width: 100%;
`;

const InputTitle = styled.div`
    font-size: 14px;
    font-weight: 600;
    color: #348a8c;
    margin-bottom: 8px;
`;

const InputWrap = styled.div`
    display: flex;
    border-radius: 8px;
    padding: 16px;
    margin-top: 8px;
    background-color: white;
    border: 1px solid #e2e0e0;

    &:focus-within {
        border: 1px solid #348a8c;
    }

    input {
        width: 100%;
        outline: none;
        border: none;
        height: 24px;
        font-size: 14px;
        font-weight: 400;

        &::placeholder {
            color: #dadada;
        }
    }
`;

const ErrorMessageWrap = styled.div`
    margin-top: 8px;
    color: #ef0000;
    font-size: 12px;
`;

const BottomButton = styled.button`
    width: 100%;
    height: 48px;
    border: none;
    font-weight: 700;
    background-color: #348a8c;
    border-radius: 64px;
    color: white;
    margin-bottom: 16px;
    cursor: pointer;

    &:disabled {
        background-color: #dadada;
        color: white;
    }
`;

const AutoLoginWrap = styled.div`
    display: flex;
    align-items: center;
    justify-content: flex-start; /* 왼쪽 정렬 */
    margin-bottom: 16px;

    label {
        display: flex;
        align-items: center;
        font-size: 12px;
        color: #262626;
    }

    input {
        margin-right: 8px; /* 체크박스와 텍스트 간의 간격 조정 */
    }
`;

const FindInfoWrap = styled.div`
    display: flex;
    justify-content: flex-end; /* 오른쪽 정렬 */
    margin-bottom: 16px;

    a {
        font-size: 12px;
        color: #348a8c;
        text-decoration: none;
        margin-left: 8px; /* 링크 간의 간격 조정 */
    }
`;

const SocialLoginWrap = styled.div`
    text-align: center;
    margin-top: 16px;

    p {
        font-size: 14px;
        color: #262626;
        margin-bottom: 8px;
    }

    img {
        width: 36px;
        height: 36px;
        margin: 0 8px;
        cursor: pointer;
    }
`;

const Space = styled.div`
    margin-top: 32px;
`;

export default function Login() {
    const [email, setEmail] = useState('');
    const [pw, setPw] = useState('');

    const [emailValid, setEmailValid] = useState(false);
    const [pwValid, setPwValid] = useState(false);
    const [notAllow, setNotAllow] = useState(false);

    const navigate = useNavigate();
    const { login } = useAuth();

    const handleEmail = (e) => {
        setEmail(e.target.value);
        const regex = 
            /^(([^<>()\[\].,;:\s@"]+(\.[^<>()\[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\].,;:\s@"]{2,})$/i;
        if (regex.test(e.target.value)) {
            setEmailValid(true);
        }
        else {
            setEmailValid(false);
        }
    }

    const handlePassword = (e) => {
        setPw(e.target.value);
        const regex = 
            /^(?=.*[a-zA-z])(?=.*[0-9])(?=.*[$`~!@$!%*#^?&\\(\\)\-_=+])(?!.*[^a-zA-z0-9$`~!@$!%*#^?&\\(\\)\-_=+]).{8,20}$/;
        if (regex.test(e.target.value)) {
            setPwValid(true);
        }
        else {
            setPwValid(false);
        }
    }

    const onClickConfirmButton = () => {
        if(email === User.email && pw === User.pw) {
            alert('로그인에 성공했습니다.');
            login();
            navigate('/main');
        }
        else if (email === User.email && pw != User.pw) {
            alert('비밀번호가 맞지 않습니다.');
        }
        else {
            alert('등록되지 않은 회원입니다.');
        }
    }

    useEffect(() => {
        if(emailValid && pwValid) {
            setNotAllow(false);
            return;
        }
        setNotAllow(true);
    }, [emailValid, pwValid]);

    return (
        <Page>
            <TitleWrap>
                <img src={logo} alt="SCHOLLI Logo" />
                <h1>로그인</h1>
            </TitleWrap>
            <ContentWrap>
                <InputTitle>이메일 주소</InputTitle>
                <InputWrap>
                    <input 
                        type='text'
                        placeholder="user@example.com"
                        value={email}
                        onChange={handleEmail}/>
                </InputWrap>
                <ErrorMessageWrap>
                    {!emailValid && email.length > 0 && (
                        <div>올바른 이메일을 입력해주세요.</div>
                    )}
                </ErrorMessageWrap>

                <InputTitle style={{ marginTop: "26px" }}>비밀번호</InputTitle>
                <InputWrap>
                    <input 
                        type='password'
                        placeholder="password"
                        value={pw}
                        onChange={handlePassword}/>
                </InputWrap>
                <ErrorMessageWrap>
                    {!pwValid && pw.length > 0 && (
                        <div> 영문, 숫자, 특수문자 포함 8자 이상 입력해주세요.</div>
                    )}
                </ErrorMessageWrap>

                <BottomButton style={{ marginTop: "26px" }} onClick={onClickConfirmButton} disabled={notAllow}>
                    로그인
                </BottomButton>
            </ContentWrap>

            <AutoLoginWrap>
                <label>
                    <input type="checkbox" /> 자동 로그인
                </label>
            </AutoLoginWrap>

            <FindInfoWrap>
                <a href="#!">아이디 찾기</a>
                <a href="#!">비밀번호 찾기</a>
            </FindInfoWrap>


            <SocialLoginWrap>
                <p>sign up with</p>
                <img src="apple-logo.png" alt="Apple" />
                <img src="google-logo.png" alt="Google" />
                <img src="kakao-logo.png" alt="Kakao" />
            </SocialLoginWrap>
        </Page>
    )
}
