import React from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import NavBar from "../ui/NavBar";
import Button from "../ui/Button";

const Wrapper = styled.div`
    padding: 16px;
    width: calc(100% - 32px);
    min-height: 200vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;
    background: linear-gradient(to bottom, #ffffff, #348a8c, #0f4c4f);
`;

const Title = styled.h1`
    font-size: 40px; /* 글씨 크기를 키움 */
    margin-top: 128px; /* 위로 올리기 위해 상단 여백 조정 */
    text-align: center;
    color: #2f6877;
`;
const Space = styled.div`
    margin-top: 32px;
`;
const Rectangle = styled.div`
    width: 100%;
    max-width: 90%; /* 가로 길이를 창 크기에 맞게 조절 */
    height: 400px; /* 세로 길이를 늘림 */
    background-color: white;
    padding: 40px; /* 버튼 주위에 원하는 공간을 만들기 위해 패딩 조정 */
    border-radius: 8px;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1); /* 그림자 효과 */
    margin-bottom: 16px; /* 사각형 간의 간격 */
    display: flex;
    flex-direction: column;
    justify-content: flex-end; /* 하단 정렬 */
    align-items: flex-start; /* 좌측 정렬 */
`;

const Text = styled.p`
    margin: 0;
    padding-bottom: 20px; /* 버튼과의 간격을 위해 패딩 추가 */
    font-size: 30px;
    font-type: bold;
    color: #333;
`;

const StyledButton = styled(Button)`
    padding: 8px 16px;
    border: 1px solid #000; /* Black border */
    border-radius: 4px; /* Rounded corners for the button */
    background-color: #f0f0f0; /* Light grey background */
    color: #000; /* Black text */
    margin-top: auto;
`;


function MainPage(props) {
    const navigate = useNavigate();

    return (
        <>
        <NavBar />
            <Wrapper>
                <Space />
                <Space />
                <Space />
                <Title>나의 맞춤 장학금을 만나다</Title>
                <Space />
                <Space />
                <Space />
                <Space />
                <Rectangle style={{ marginTop: "16px" }}> {/* 첫 번째 Rectangle 위치를 위로 이동 */}
                    <Text>다양한 기관과 단체의
                        <br/>장학금을 확인해보세요</Text> {/* 첫 번째 Rectangle의 문구 */}
                        <Space />
                        <Space />
                        <Space />
                        <Space />
                    <StyledButton
                        title='전체 장학금 목록 보기'
                        onClick={() => {
                            navigate('/login');
                        }}
                    />
                </Rectangle>
                <Space />
                <Space />
                <Rectangle>
                    <Text>두번째 문구</Text> {/* 두 번째 Rectangle의 문구 */}
                    <StyledButton
                        title='추천 장학금 목록 보기'
                        onClick={() => {
                            navigate('/login');
                        }}
                    />
                </Rectangle>
            </Wrapper>
        </>
    );
}

export default MainPage;
