import React from "react";
import NavBar from "../ui/NavBar";
import scholarships from "../data";

const styles = {
    container: {
        margin: "20px",
        fontFamily: "Arial, sans-serif"
    },
    searchBar: {
        display: "flex",
        justifyContent: "space-between",
        marginBottom: "20px"
    },
    searchInput: {
        flex: 1,
        padding: "10px",
        fontSize: "16px",
        border: "1px solid #ddd",
        borderRadius: "4px"
    },
    sortButton: {
        marginLeft: "10px",
        padding: "10px 20px",
        fontSize: "16px",
        backgroundColor: "#007bff",
        color: "white",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer"
    },
    sortButtonHover: {
        backgroundColor: "#0056b3"
    },
    table: {
        width: "100%",
        borderCollapse: "collapse"
    },
    thTd: {
        border: "1px solid #ddd",
        borderBottom: "1px solid #ddd",
        textAlign: "left",
        padding: "12px"
    },
    th: {
        backgroundColor: "#f2f2f2",
        fontWeight: "bold"
    },
    infoButton: {
        backgroundColor: "#4CAF50",
        color: "white",
        border: "none",
        padding: "8px 16px",
        textAlign: "center",
        textDecoration: "none",
        display: "inline-block",
        fontSize: "14px",
        margin: "4px 2px",
        cursor: "pointer",
        borderRadius: "4px"
    },
    infoButtonHover: {
        backgroundColor: "#45a049"
    },
    pagination: {
        marginTop: "20px",
        textAlign: "center"
    },
    paginationSpan: {
        cursor: "pointer",
        padding: "8px 16px",
        textDecoration: "none",
        color: "#007bff"
    },
    paginationSpanHover: {
        textDecoration: "underline"
    }
};

function EntireScholar(props) {
    return (
        <>
            <NavBar/>
            <div style={styles.container}>
                <div style={styles.searchBar}>
                    <input type="text" placeholder="검색" style={styles.searchInput} />
                    <button style={styles.sortButton}>기한 순 ▼</button>
                </div>
                <table style={styles.table}>
                    <thead>
                        <tr>
                            <th style={{ ...styles.thTd, ...styles.th }}>장학 재단명</th>
                            <th style={{ ...styles.thTd, ...styles.th }}>장학 사업명</th>
                            <th style={{ ...styles.thTd, ...styles.th }}>기한</th>
                            <th style={{ ...styles.thTd, ...styles.th }}>이전 수혜자 정보</th>
                        </tr>
                    </thead>
                    <tbody>
                    {scholarships.map((scholarship, index) => (
                            <tr key={index}>
                                <td style={styles.thTd}>{scholarship.scholarname}</td>
                                <td style={styles.thTd}>{scholarship.businessname}</td>
                                <td style={styles.thTd}>{scholarship.period}</td>
                                <td style={styles.thTd}><button style={styles.infoButton}>정보 보러가기</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div style={styles.pagination}>
                    <span style={styles.paginationSpan}>1 2 3 4 5</span>
                </div>
            </div>
        </>
    );

}
export default EntireScholar;
