import React, { useState } from "react";
import NavBar from "../ui/NavBar";
import scholarships from "../data";
import emptyheart from "../ui/emptyheart.jpeg";
import filledheart from "../ui/filledheart.jpeg";


const styles = {
    container: {
        margin: "20px",
        fontFamily: "Arial, sans-serif"
    },
    searchBar: {
        display: "flex",
        justifyContent: "space-between",
        marginBottom: "20px",
        position: "relative", // 상대 위치를 기준으로 설정
        zIndex: 1 // 드롭다운 메뉴와 겹치지 않도록 설정
    },
    searchInput: {
        flex: 1,
        padding: "10px",
        fontSize: "16px",
        border: "1px solid #ddd",
        borderRadius: "4px"
    },
    sortButton: {
        marginLeft: "10px",
        padding: "10px 20px",
        fontSize: "16px",
        backgroundColor: "#348a8c",
        color: "white",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer",
        position: "relative",  // 상대 위치를 기준으로 설정
        zIndex: 2
    },
    dropdown: {
        position: "absolute",
        top: "calc(100% + 4px)", // 버튼 바로 아래에 위치하도록 설정
        right: "0",
        backgroundColor: "white",
        boxShadow: "0 8px 16px rgba(0,0,0,0.2)",
        zIndex: 3,
        borderRadius: "4px",
        overflowY: "auto", // 세로 스크롤바 추가
        maxHeight: "200px" // 최대 높이 설정
    },
    dropdownItem: {
        padding: "10px 20px",
        cursor: "pointer",
        whiteSpace: "nowrap"
    },
    dropdownItemHover: {
        backgroundColor: "#f1f1f1"
    },
    table: {
        width: "100%",
        borderCollapse: "collapse"
    },
    thTd: {
        borderTop: "1px solid #348a8c",
        borderBottom: "1px solid #ddd",
        borderLeft: "0",
        borderRight: "0",
        textAlign: "left",
        padding: "12px",

    },
    th: {
        fontWeight: "bold",
        borderTop: "0"
    },
    firstThTd: {
        borderBottom: "2px solid #348a8c", 
        textAlign: "left",
        padding: "12px"
    },
    infoButton: {
        backgroundColor: "#348a8c",
        color: "white",
        border: "none",
        padding: "8px 16px",
        textAlign: "center",
        textDecoration: "none",
        display: "inline-block",
        fontSize: "14px",
        margin: "4px 2px",
        cursor: "pointer",
        borderRadius: "4px"
    },
    flexContainer: {
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between" 
    },
    heartButton: {
        background: "none",
        border: "none",
        cursor: "pointer",
        marginLeft: "60px"

    },
    heartImage: {
        width: "20px",
        height: "20px"
    },
    pagination: {
        marginTop: "20px",
        textAlign: "center"
    },
    paginationSpan: {
        cursor: "pointer",
        padding: "8px 16px",
        textDecoration: "none",
        color: "#348a8c"
    },
    paginationSpanHover: {
        textDecoration: "underline"
    }
};


function RecomScholar(props) {
    return (
        <>
            <NavBar/>
        </>
    );

}
export default RecomScholar;
