import React from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import NavBar from "../ui/NavBar";
import Button from "../ui/Button";

const Wrapper = styled.div`
    padding: 16px;
    width: calc(100% - 32px);
    min-height: 200vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    background: linear-gradient(to bottom, #ffffff, #348a8c, #0f4c4f);
`;


const Rectangle = styled.div`
    width: 100%;
    max-width: 300px;
    background-color: white;
    padding: 40px; /* Adjust the padding to create the desired space around the button */
    border-radius: 8px;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1); /* Shadow effect */
    margin-bottom: 16px; /* Space between rectangles */
    margin-top: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const StyledButton = styled(Button)`
    padding: 8px 16px;
    border: 1px solid #000; /* Black border */
    border-radius: 4px; /* Rounded corners for the button */
    background-color: #f0f0f0; /* Light grey background */
    color: #000; /* Black text */
`;


function MainPage(props) {
    const navigate = useNavigate();

    return (
        <>
        <NavBar />
            <Wrapper>
                <Rectangle style={{ marginTop: "16px" }}> {/* 첫 번째 Rectangle 위치를 위로 이동 */}
                    <StyledButton
                        title='전체 장학금 목록 보기'
                        onClick={() => {
                            navigate('/login');
                        }}
                    />
                </Rectangle>
                <Rectangle>
                    <StyledButton
                        title='추천 장학금 목록 보기'
                        onClick={() => {
                            navigate('/login');
                        }}
                    />
                </Rectangle>
            </Wrapper>
        </>
    );
}

export default MainPage;
